var Phoenix = cc.LayerGradient.extend({
    onEnter:function () {
        this._super();
        this.init(cc.c4b(0,0,0,255), cc.c4b(98,99,117,255));

        // Create particle emitter from plist file
        var emitter = cc.ParticleSystem.create(res.Phoenix_plist);
        this.addChild(emitter, 10);

        // Set particle source position
        var size = cc.Director.getInstance().getWinSize();
        emitter.setPosition(size.width / 2, size.height / 2 - 50);
    }
});

Phoenix.create = function() {
    var layer = new Phoenix();
    return layer;
}