var ProgressTimerVertical = cc.Layer.extend({
   init:function(){
       this._super();
       var winSize = cc.Director.getInstance().getWinSize();
       var to1 = cc.ProgressTo.create(5, 100);
       var to2 = cc.ProgressTo.create(5, 75);

       var left = cc.ProgressTimer.create(cc.Sprite.create(res.powered_png));
       left.setType(cc.PROGRESS_TIMER_TYPE_BAR);
       left.setPosition(200, winSize.height / 2);
       left.runAction(to1);
       this.addChild(left);
       //    Setup for a bar starting from the bottom since the midpoint is 0 for the y
       left.setMidpoint(cc.p(0, 0));
       //    Setup for a vertical bar since the bar change rate is 0 for x meaning no horizontal change
       left.setBarChangeRate(cc.p(0, 1));

       var right = cc.ProgressTimer.create(cc.Sprite.create(res.powered_png));
       right.setType(cc.PROGRESS_TIMER_TYPE_BAR);
       right.setReverseDirection(true);
       right.setPosition(winSize.width - 200, winSize.height / 2);
       right.runAction(to2);
       this.addChild(right);
       //    Setup for a bar starting from the bottom since the midpoint is 0 for the y
       right.setMidpoint(cc.p(0, 1));
       //    Setup for a vertical bar since the bar change rate is 0 for x meaning no horizontal change
       right.setBarChangeRate(cc.p(0, 1));
       return true;
   }
});
ProgressTimerVertical.create = function(args){
    var layer = new ProgressTimerVertical();
    return layer.init() ? layer : null;
}